								  end + 													
								  case when @e2 - @r2 < 0 then 0		-- subject 2 excess		
								 	   else @e2 - @r2				 
								  end +															
								  @e3 > @r3 then @r3					-- other non-required subjects					
								  else 
								  case when @e1 - @r1 < 0 then 0		
									   else @e1 - @r1 
								  end + 													
								  case when @e2 - @r2 < 0 then 0		
									   else @e2 - @r2 
								  end +															
								  @e3
							 end) < 0 then 0
							 else (@r1 + @r2 + @r3) -
							(case when @e1 > @r1 then @r1			
								  else @e1							
							 end +
							 case when @e2 > @r2 then @r2			
								  else @e2							
							 end +
