									   else @e1 - @r1 
								  end + 													
								  case when @e2 - @r2 < 0 then 0		
									   else @e2 - @r2 
								  end +															
								  @e3
							 end)
						end
                   ------------------------------------------
              end -- end main case statement
    ) 

