CREATE view [dbo].[JWB_Discipline]
as
----------------------------------------------------------------
select
     cast(b.syear as varchar(4)) + '-' + cast(b.syear+1 as varchar(4)) as SchoolYear
    ,cast(b.syear as varchar(4)) as syear
    ,d.Stu_SIS_ID
