CREATE view [dbo].[naviance_groups]
AS
-----------------------------
select
	 s.stu_id
	,'"ESE"'					as [Group]
--select *
from DS_Students as s
where s.stu_username is not null
  and s.active = 1
  and s.alt_assessment in (
							 'Alt Assessment will be Administered [Y]'
							,'Student will be assessed using FSAA-Datafolio [D]'
