  and s.active = 1
  and c.avid > 0

union all

select
	 s.stu_id
	,'"IB"'					as [Group]
from DS_Students as s
join (
		select 
		 s.stu_id
		,sum(s.avid_enr) as avid
		,sum(s.ib_enr) as ib
