                      / Cast(students AS real) AS perMthlv5
FROM         (SELECT     CASE WHEN levelname = 'Elementary' THEN '10100' WHEN levelname = 'Middle' THEN '10200' WHEN levelname = 'High' THEN '10300' ELSE
